from collections import deque

SIZE_MARKER = 14 # Change to 4 for part 1, change to 14 for part 2

def markerPos(buffer):
  marker = deque([c for c in buffer[:SIZE_MARKER-1]], SIZE_MARKER)
  i = SIZE_MARKER-1
  for char in buffer[SIZE_MARKER-1:]:
    marker.append(char)
    i += 1
    if validStartOfPacketMarker(marker):
      return i

# It's a valid marker if all characters are different
def validStartOfPacketMarker(marker):
  for i in range(0, SIZE_MARKER-1):
    for j in range(i+1, SIZE_MARKER):
      if marker[i] == marker[j]:
        return False
  return True


if __name__ == '__main__':
  # I know, I was lazy :D
  print("marker received at position", markerPos(""))